WHERE `Id`=7837 AND `Locale`='zhCN';

UPDATE `broadcast_text_locale` SET
	`Text_lang`='在我袒露秘密之前，你得证明自己的价值！我会对你进行测试的。'	-- You'll need to prove your worthiness before I go giving away my secrets! I've got just the test for you...
WHERE `Id`=7838 AND `Locale`='zhCN';

